import db from '../../lib/db';
import admin from 'firebase-admin';

const saveStatus = async(req, res) => {
    await db
    .collection('test')
    .add({
        test: 1,
        coordinates: new admin.firestore.GeoPoint(-20.12, -45.829)
    })

    console.log(req.body);

    res.send({ ok: true });
}

export default saveStatus
