import React from 'react';
import auth0 from '../lib/auth0';

const CreateStatus = () => {
    return (
        <div>
        <h1>Create Status</h1>
        <button>Get my location</button>
        </div>
        )
    }
    
    export default CreateStatus;

    export async function getServerSideProps({ req, res }){
        const session = await auth0.getSession(req);
        
        if(session){
            return {
                props: {
                    isAuth: true,
                    user: session.user
                }
            }
        }
        
        return {
            props: {
                isAuth: false,
                user: {}
            }
        }
    }
